mapper.select:
select
commentId, solutionId, commentContent, userId, parentId, commentDatetime, commentLikeNumber
from leetcode.solutioncomment


        and commentId = #{commentid}


        and solutionId = #{solutionid}


        and commentContent = #{commentcontent}


        and userId = #{userid}


        and parentId = #{parentid}


        and commentDatetime = #{commentdatetime}


        and commentLikeNumber = #{commentlikenumber}
